css:
@page {
       size: 100mm 25mm; /* Page size = 2 labels side by side */
       margin: 0mm;
   }
   body {
       margin: 0;
       font-family: Arial, sans-serif;
   }
   .barcode-labels {
       width: 100%;
   }
   .labels-row {
       display: flex;
       justify-content: space-between;
       width: 100%;
       height: 100%;
   }
   .label {
       width: 48mm;
       height: 25mm;
       padding: 1mm;
       box-sizing: border-box;
       font-size: 8px;
       display: flex;
       flex-direction: column;
       justify-content: center;
       align-items: center;
       page-break-inside: avoid;
   }
   .barcode-img img {
       width: 90%;
       height: auto;
       max-height: 13mm;
       object-fit: contain;

   }
   .item-name {
       font-size: 14px;
       margin-bottom: 4px;
       text-align: center;
       font-weight: bold;
   }
   .barcode-number {
       font-size: 14px;
       letter-spacing: 5px;
       margin-top:2px;
       text-align: center;
   }
   @media print {
       body { margin: 0; padding: 0; }
       .no-print { display: none; }
   }

html:
 {%- set today = frappe.utils.now_datetime().strftime("%y%m%d") %}
{%- set reprint = doc.reprint_barcode %}
{%- set total_qty = doc.received_qty | int %}
{%- set batch_no = 1 %}

{%- if reprint %}
    {# Print only the entered barcode #}
    <div class="labels-row">
        <div class="label">
            <div class="item-name"><strong>{{ doc.item_name }}</strong></div>
            <div class="barcode-img">
                <img src="https://bwipjs-api.metafloor.com/?bcid=code128&text={{ reprint }}&scale=2&height=10&includetext=false" />
            </div>
            <div class="barcode-number">
                 {{ reprint }}
            </div>
        </div>
    </div>
{%- else %}
    {# Default: print full batch as before #}
    {%- for i in range(total_qty) %}
        {%- if loop.index0 % 2 == 0 %}
            <div class="labels-row">
        {%- endif %}

        {%- set serial_no = i + 1 %}
        {%- set serial_str = "{:04d}".format(serial_no) %}
        {%- set barcode = "{}B{}{}".format(today, batch_no, serial_str) %}

        <div class="label">
            <div class="item-name"><strong>{{ doc.item_name }}</strong></div>
            <div class="barcode-img">
                <img src="https://bwipjs-api.metafloor.com/?bcid=code128&text={{ barcode }}&scale=2&height=10&includetext=false" />
            </div>
            <div class="barcode-number">
                {{ barcode }}

            </div>
        </div>

        {%- if loop.index0 % 2 == 1 or loop.last %}
            </div>
            {% if not loop.last %}<div style="page-break-after: always;"></div>{% endif %}
        {%- endif %}
    {%- endfor %}
{%- endif %}
