css:

          @page {
            size: A4;
            margin: 10mm;
        }

        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 15px;
        }

        .a4-border {
            border: 1px solid #000;
            min-height: 277mm;
            box-sizing: border-box;
        }

        .header {
            text-align: center;
            margin-bottom: 20px;
        }

        .company-name {
            font-size: 24px;
            font-weight: bold;
            margin-bottom: 5px;
            text-transform: uppercase;
        }

        .company-address {
            font-size: 14px;
            line-height: 1.4;
        }

        .contact-info {
            font-size: 13px;
            margin-top: 5px;
        }

        .details-row {
            display: flex;
            border: 1px solid black;
            font-size: 13px;
        }

        .details-column {
            flex: 1;
            padding: 10px;
            border-right: 1px solid black;
        }

        .details-column:last-child {
            border-right: none;
        }

        .label-row {
            display: flex;
            justify-content: space-between;
            margin-bottom: 3px;
        }

        .label-text {
            flex: 1;
            font-weight: bold;
            text-align: left;
        }

        .label-colon {
            flex: 0 0 10px;
            text-align: center;
            font-weight: bold;
        }

        .label-value {
            flex: 1;
        }

        .section-header {
            display: flex;
            font-weight: bold;
            text-align: center;
            border-left: 1px solid black;
            border-right: 1px solid black;
            border-top: 1px solid black;
            border-bottom: 1px solid black;

        }


        .section-header div {
            flex: 1;
            padding: 6px 10px;
            border-right: 1px solid black;
        }

        .section-header div:last-child {
            border-right: none;
        }

        .customer-row {
            display: flex;
            border: 1px solid black;
            border-top: none;
            font-size: 13px;
        }

        .customer-column {
            flex: 1;
            padding: 10px;
            border-right: 1px solid black;
        }

        .customer-column:last-child {
            border-right: none;
        }

        /* Item Table */
        table {
            width: 100%;
            border-collapse: collapse;
            font-size: 12px;

        }

        th, td {
            border: 1px solid black;
            padding: 6px;
            text-align: center;
        }

        th {
            font-weight: bold;
            color:black;

        }

        td.left {
            text-align: left;
        }

        td.right {
            text-align: right;
        }
        .total-qty{
            background-color:blue;
            color:red;
            margin-left:0px;
      }
      td strong {
    display: inline-block;
    width: 100%;
}
