       display: flex;
       justify-content: space-between;
       width: 100%;
       height: 100%;
   }
   .label {
       width: 48mm;
       height: 25mm;
       padding: 1mm;
       box-sizing: border-box;
       font-size: 8px;
       display: flex;
       flex-direction: column;
       justify-content: center;
       align-items: center;
       page-break-inside: avoid;
   }
   .barcode-img img {
       width: 90%;
       height: auto;
       max-height: 13mm;
       object-fit: contain;

   }
   .item-name {
       font-size: 14px;
       margin-bottom: 4px;
       text-align: center;
