 {%- set serials = doc.serial_no.split(",") if doc.serial_no else [] %}

{%- for serial in serials %}
    {%- if loop.index0 % 2 == 0 %}
        <div class="labels-row">
    {%- endif %}

    <div class="label">
        <div class="item-name"><strong>{{ doc.item_name }}</strong></div>
        <div class="barcode-img">
            <img src="https://bwipjs-api.metafloor.com/?bcid=code128&text={{ serial }}&scale=2&height=10&includetext=false" />
        </div>
        <div class="barcode-number">{{ serial }}</div>
    </div>

    {%- if loop.index0 % 2 == 1 or loop.last %}
        </div>
        {% if not loop.last %}<div style="page-break-after: always;"></div>{% endif %}
    {%- endif %}
{%- endfor %}

